package week4;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 85;
		
		if(score >= 90) {//True
			System.out.println("90점 이상");
			System.out.println("A등급");
		}
		else {//False
			System.out.println("90 미만");
			System.out.println("B등급");
		}
	}

}
